import React, { useState } from 'react';
import SubscribePayLanding from './components/SubscribePayLanding';
import DashboardMain from './components/Dashboard/DashboardMain';
import './index.css';

function App() {
  const [currentView, setCurrentView] = useState<'landing' | 'dashboard'>('landing');

  return (
    <div className="App">
      {currentView === 'landing' ? (
        <div>
          <SubscribePayLanding />
          <div className="fixed bottom-6 right-6">
            <button
              onClick={() => setCurrentView('dashboard')}
              className="bg-primary hover:bg-primary-dark text-white px-6 py-3 rounded-lg font-semibold shadow-lg hover:shadow-xl transition-all duration-200 hover:scale-105"
            >
              View Dashboard
            </button>
          </div>
        </div>
      ) : (
        <div>
          <DashboardMain />
          <div className="fixed bottom-6 right-6">
            <button
              onClick={() => setCurrentView('landing')}
              className="bg-white hover:bg-gray-50 text-primary border-2 border-primary px-6 py-3 rounded-lg font-semibold shadow-lg hover:shadow-xl transition-all duration-200 hover:scale-105"
            >
              Back to Landing
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
